import React, { Component } from 'react';
import { ScrollView, StyleSheet, Text, View, CheckBox} from 'react-native';
import UserInput from "../components/UserInput";
import AddReminder from "../components/AddReminder";


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'gray',
        alignItems: 'center',
        justifyContent: 'center'
    },
    button: {
        backgroundColor: "blue",
        padding: 20,
        borderRadius: 5,
        marginTop: "1rem"
    },
    buttonText: {
        fontSize: 20,
        color: '#fff',
        textAlign: "center"
    },
    checkboxContainer: {
        flexDirection: "row",
        marginBottom: 20,
    },
    checkbox: {
        alignSelf: "center",
    },
    label: {
        margin: 8,
    }

});

class AddMate3 extends Component {

    constructor(props) {
        super(props)

        this.state = {
            mateName: "",
            species: "",
            reminderSelected: false
        }
    }


    handleInputChange = event => {
        console.log(`handleInputChange ${event}`);
        const { name, value } = event.target;
        this.setState({
            [name]: value
        }, function() {
            console.log(this.state)
        })
    };

    setSelection = () => {
        if (this.state.reminderSelected === false) {
            this.setState({
                reminderSelected: true
            })
        }
        else {
            this.setState({
                reminderSelected: false
            })
        }

    }

    render() {
        return (
            <View style={styles.container}>
                <View >
                    <Text>Step 3:</Text>
                    <Text>Upload a photo (optional)</Text>


                </View>

                <View>
                    <Text>Step 4:</Text>
                    <Text>Tell us a little bit about them</Text>

                    <Text>Name</Text>
                    <UserInput
                        text= {this.state.mateName}
                        onChangeText= {this.handleInputChange}
                        placeholder= "Chowdie"
                        name="mateName"
                    />

                    <Text>Species</Text>
                    <UserInput 
                        text= {this.state.species}
                        onChangeText= {this.handleInputChange}
                        placeholder= "Gold Fish"
                        name="species"
                    />
                </View>

                <View style={styles.checkboxContainer}>
                    <CheckBox
                        value={this.state.reminderSelected}
                        onValueChange={this.setSelection}
                        style={styles.checkbox}
                    />

                    <Text style={styles.label}>Add Reminder</Text>
                </View>


            </View>
        );
    }
}

export default AddMate3;
