import React, { Component } from 'react';
import { ScrollView, StyleSheet, Text, View, TouchableOpacity, Alert} from 'react-native';
import UserInput from "../components/UserInput";


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'gray',
        alignItems: 'center',
        justifyContent: 'center'
    },
    button: {
        backgroundColor: "blue",
        padding: 20,
        borderRadius: 5,
        marginTop: "1rem"
    },
    buttonText: {
        fontSize: 20,
        color: '#fff',
        textAlign: "center"
    }

});

class AddMate3 extends Component {

    constructor(props) {
        super(props)

        this.state = {
            selectedTank: "hello",
            tankOptions: [ {id: 1, name: "Tank 1"}, {id: 2, name: "Tank 2"}, {id: 3, name: "Tank 3"} ],
            // tankOptions: ["Tank 2", "Tank 3", "Tank 4"]
            mateTypes: ["Plant", "Fish", "Amphibian", "Invertebrate"],
            selectedType: "",
            mateName: "",

        }
    }

    setSelectedTank = (itemValue, itemIndex) => {
        this.setState({
            selectedTank: itemValue
        }, function() {
            console.log(this.state.selectedTank)
        })
    }

    handleInputChange = event => {
        const { name, value } = event.target;
        this.setState ({
            [name]: value
        }, function () {
            console.log(this.state.mateName)
        })
    };

    render() {
        return (
            <View style={styles.container}>
                <View >
                    <Text>Step 3:</Text>
                    <Text>Upload a photo (optional)</Text>


                </View>

                <View>
                    <Text>Step 4:</Text>
                    <Text>Tell us a little bit about them</Text>

                    <UserInput 
                        mateName= {this.state.mateName}
                        onChangeText= {this.handleInputChange}
                        placeholder= "Chowdie"
                        name= "mateName"
                    />
                </View>


            </View>
        );
    }
}

export default AddMate3;
