class AddMate1 extends Component {

    constructor(props) {
        super(props)

        this.state = {
            selectedTank: "hello",
            tankOptions: ["Tank 2", "Tank 3", "Tank 4"]
        }
    }

    setSelectedTank = (itemValue, itemIndex) => {
        this.setState({
            selectedTank: itemValue
        }, function() {
            console.log("AddMate setState")
        })
        console.log(this.state.selectedTank)
    }


    render() {
        return (
            <View >
                <Text>
                    Add A Tank Mate
